import Header from "./Header";
import { Link, useNavigate , useParams} from 'react-router-dom'
import { useEffect, useState } from "react";
import axios from "axios";
import Categories from "./categories";
import { FaHeart } from "react-icons/fa";
import './Home.css' ;


function ProductDetail(){
    
    const p = useParams() ;
    console.log(p.id) ;
    const [productDetail , setproductDetail] = useState()
    useEffect(() => {
        
        const url = 'http://localhost:4000/get-productDetail:'+p.id;
        axios.get(url)
            .then((res) => {
                
                if (res.data.products) {
                    console.log(res.data.products) ;
                }
            })
            .catch((err) => {
                
                alert('server error');
            })
    }, []) ;


     return (
        <div className="d-flex justify-content-center flex-wrap">
        {
           
                    <div key={item._id} className="card m-3">
                        <img width="400px" height="200px" src={'http://localhost:4000/' + item.pimage}></img>
                        <div className="icon-con" onClick={()=>handleLike(item._id)}>
                            <FaHeart className="icons" />
                        </div>
                        <p className="m-2">{item.pname} | {item.ptype}</p>
                        <p className="m-2 text-primary">{item.pdesc}</p>
                        <h3 className="m-2 text-danger">{item.pprice}</h3>
                    </div>

               
        }
    </div>
     
     ) 
}

 export default ProductDetail ;
