import Header from "./Header";
import {Link , useNavigate} from 'react-router-dom'
import { useEffect, useState } from "react";
import axios from "axios";
function Home () {
    const navigate = useNavigate() ;
    const [products , setproducts] = useState([]) ;
    const [search , setsearch] = useState('') ;
    // useEffect(()=>{
    //           if(!localStorage.getItem('token')) {
    //              navigate('/login') ;
    //           }
    // } , []) ;


    useEffect(()=>{
         const url = 'http://localhost:4000/get-products' ;
          axios.get(url)
          .then((res)=>{
            //  console.log("RES =>>" ,res) ;
             if(res.data.products) {
                setproducts(res.data.products) ;
             }
          })
          .catch((err)=>{
             console.log(err) ;
             alert('server error') ;
          })
} , [] ) ;



   const handlesearch = (value) =>{
    //   console.log(search) ;
      setsearch(value) ;
   }

   const handlesearchbtn = () => {
      
      let  searchProducts = products.filter((item) => {
           if(item.pname.includes(search) || item.pdesc.includes(search) || item.ptype.includes(search)) {
                return item ;
           }
      })
       setproducts(searchProducts) ;
   }

   

    return(
        <div>
            <Header search = {search}  handlesearch = {handlesearch} handlesearchbtn = {handlesearchbtn}/>
           
            {!!localStorage.getItem('token') && <Link to = '/add-product'>ADD PRODUCT</Link> }
                
                         
            <h2>MY PRODUCTS:</h2>
            <div className="d-flex justify-content-center flex-wrap">
            {
                products && products.length > 0 && 
                products.map((item ,index)=>{
                    return (
                         <div className="card m-3">
                             <img  width = "400px" height="200px" src = {'http://localhost:4000/' + item.pimage}></img>
                             <p className = "m-2">{item.pname} | {item.ptype}</p>
                             <p className = "m-2 text-primary">{item.pdesc}</p>
                             <h3 className = "m-2 text-danger">{item.pprice}</h3>
                         </div>
                        
                    )
                })
            }
            </div>

        
           
        </div>
    )
}

export default Home ;
