import './Header.css';
import './Home.css'
import { useEffect, useState } from "react";
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios';
function LikeProducts(props) {
    const navigate = useNavigate();
    const userId = localStorage.getItem('userId') ;
    const [like, setLike] = useState([]) ;
    useEffect(() => {
        const url = 'http://localhost:4000/get-likeProducts:'+userId;
        axios.get(url)
            .then((res) => {
                console.log(res) ;
                
            })
            .catch((err) => {
               alert('server error');
            })

            
    }, []) ; 


    return (
          <div>
             <button className='search-btn'> LIKE PRODUCTS</button>

             <div className="d-flex justify-content-center flex-wrap">
                {
                    products && products.length > 0 &&
                    products.map((item, index) => {
                        return (
                            <div key={item._id} className="card m-3">
                                <img width="400px" height="200px" src={'http://localhost:4000/' + item.pimage}></img>
                                <div className="icon-con" onClick={()=>handleLike(item._id)}>
                                    <FaHeart className="icons" />
                                </div>
                                <p className="m-2">{item.pname} | {item.ptype}</p>
                                <p className="m-2 text-primary">{item.pdesc}</p>
                                <h3 className="m-2 text-danger">{item.pprice}</h3>
                            </div>

                        )
                    })
                }
            </div>
         </div>

       



        
    )
}

export default LikeProducts;
