import Header from "./Header";
import { Link, useNavigate } from 'react-router-dom'
import { useEffect, useState } from "react";
import axios from "axios";
import Categories from "./categories";
function Home() {
    const navigate = useNavigate();
    const [products, setproducts] = useState([]);
    const [cproducts, setcproducts] = useState([]);
    const [search, setsearch] = useState('');



    useEffect(() => {
        const url = 'http://localhost:4000/get-products';
        axios.get(url)
            .then((res) => {
                //  console.log("RES =>>" ,res) ;
                if (res.data.products) {
                    setproducts(res.data.products);
                }
            })
            .catch((err) => {
                console.log(err);
                alert('server error');
            })
    }, []);



    const handlesearch = (value) => {

        setsearch(value);
    }

    const handlesearchbtn = () => {
        //   console.log("products" ,products) ;
        let searchProducts = products.filter((item) => {
            //   console.log(item) ;
            if (item.pname.toLowerCase().includes(search.toLowerCase()) ||
                item.pdesc.toLowerCase().includes(search.toLowerCase()) ||
                item.ptype.toLowerCase().includes(search.toLowerCase())) {
                return item;
            }
        })

        setcproducts(searchProducts);
    }


    const handleCategory = (value) => {
        
        let searchProducts = products.filter((item) => {
            
            if (item.ptype.toLowerCase() == (value.toLowerCase())) {
                return item;
            }
        })

        setcproducts(searchProducts);
    }

    return (
        <div>
            <Header search={search} handlesearch={handlesearch} handlesearchbtn={handlesearchbtn} />
            <Categories handleCategory={handleCategory} />
            {!!localStorage.getItem('token') && <Link to='/add-product'>ADD PRODUCT</Link>}
           
           
            {
                <h5>SEARCH PRODUCTS:</h5>

            }
            <div className="d-flex justify-content-center flex-wrap">
                {
                    products && products.length > 0 &&
                    cproducts.map((item, index) => {
                        return (
                            <div key={item._id} className="card m-3">
                                <img width="400px" height="200px" src={'http://localhost:4000/' + item.pimage}></img>
                                <p className="m-2">{item.pname} | {item.ptype}</p>
                                <p className="m-2 text-primary">{item.pdesc}</p>
                                <h3 className="m-2 text-danger">{item.pprice}</h3>
                            </div>

                        )
                    })
                }
            </div>

            <h5>ALL PRODUCTS:</h5>
            <div className="d-flex justify-content-center flex-wrap">
                {
                    products && products.length > 0 &&
                    products.map((item, index) => {
                        return (
                            <div key={item._id} className="card m-3">
                                <img width="400px" height="200px" src={'http://localhost:4000/' + item.pimage}></img>
                                <p className="m-2">{item.pname} | {item.ptype}</p>
                                <p className="m-2 text-primary">{item.pdesc}</p>
                                <h3 className="m-2 text-danger">{item.pprice}</h3>
                            </div>

                        )
                    })
                }
            </div>



        </div>
    )
}

export default Home;
