import Header from "./Header";
import { Link, useNavigate , useParams} from 'react-router-dom'
import { useEffect, useState } from "react";
import axios from "axios";
import Categories from "./categories";
import { FaHeart } from "react-icons/fa";
import './Home.css' ;


function ProductDetail(){
    
    const p = useParams() ;
   
    const [productDetail , setproductDetail] = useState('') ;
    useEffect(() => {
        
        const url = 'http://localhost:4000/get-productDetail:'+p.id;
        axios.get(url)
            .then((res) => {
                
                if (res.data.products) {
                    setproductDetail(res.data.products) ;
                }
            })
            .catch((err) => {
                
                alert('server error');
            })
    }, []) ;


     return (
        <div className="d-flex justify-content-center flex-wrap">
                   
                    {productDetail && productDetail.pname }
           
                    <div  className="card m-3">
                        <img width="500px" height="250px" src={'http://localhost:4000/' + productDetail.pimage}></img>
                        <div className="icon-con ">
                            <FaHeart className="icons" />
                        </div>
                        <p className="m-2">{productDetail.pname} | {productDetail.ptype}</p>
                        <p className="m-2 text-primary">{productDetail.pdesc}</p>
                        <h3 className="m-2 text-danger">{productDetail.pprice}</h3>
                    </div>

               
      
    </div>
     
     ) 
}

 export default ProductDetail ;
