import Header from "./Header";
import { Link, useNavigate } from 'react-router-dom'
import { useEffect, useState } from "react";
import axios from "axios";
import Categories from "./categories";
import { FaHeart } from "react-icons/fa";
import './Home.css' ;

function LikeProducts(props) {
    const navigate = useNavigate();
    const userId = localStorage.getItem('userId') ;
    const [like, setLike] = useState([]) ;
    useEffect(() => {
        const url = 'http://localhost:4000/get-likeProducts:'+userId;
        axios.get(url)
            .then((res) => {
                setLike(res) ;
                
            })
            .catch((err) => {
               alert('server error');
            })

    }, []) ; 


    return (
          <div>
              <Header />
             <Categories />
              
             <div className="d-flex justify-content-center flex-wrap">
                {
                    like && like.length > 0 &&
                    like.map((item, index) => {
                        return (
                            <div key={item} className="card m-3">
                                <img width="400px" height="200px" src={'http://localhost:4000/' + item.pimage}></img>
                               
                                <p className="m-2">{item.pname} | {item.ptype}</p>
                                <p className="m-2 text-primary">{item.pdesc}</p>
                                <h3 className="m-2 text-danger">{item.pprice}</h3>
                            </div>

                        )
                    })
                }
            </div>
             
         </div>

       



        
    )
}

export default LikeProducts ;
