import Header from "./Header";
import {Link , useNavigate} from 'react-router-dom'
import { useEffect, useState } from "react";
function AddProduct () {
    const navigate = useNavigate() ;
    
    const [pname , setpname] = useState('') ;
    const [pdesc , setpdesc] = useState('') ;
    const [pprice , setpprice] = useState('') ;
    const [ptype , setptype] = useState('') ;
    const [pimage , setpimage] = useState('') ;

    useEffect(()=>{
              if(!localStorage.getItem('token')) {
                 navigate('/login') ;
              }
    } , []) ;


    const handleApi = () =>{
         const form = new FormData()
    }

    return(
        <div>
            <Header/>
            <div className="p-3">

            <h2>ADD YOUR PRODUCT</h2>
           
            <label> Product Name</label>
            <input className="form-control" value = {pname}
             onChange={setpname}/>

            <label> Product Description</label>
            <input className="form-control"  value = {pdesc}
             onChange={setpdesc}/>

            <label> Product Price</label>
            <input className="form-control"  value = {pprice}
             onChange={setpprice}/>

            <label> Product Categiory</label>

            <select className="form-control"  value = {ptype}
             onChange={setptype}>
                <option>Bikes</option>
                <option>Mobiles</option>
                <option>Cloths</option>
                <option>Watches</option>
            </select>
            <label> Select Product Image</label>
            <input type= 'file' className="form-control" value={pimage}/>

            <button className="btn  btn-primary mt-3" onClick={handleApi}>SUBMIT</button>
            
            </div>
        </div>
    )
}

export default AddProduct ;
