import Header from "./Header";
import { Link, useNavigate } from 'react-router-dom'
import { useEffect, useState } from "react";
import axios from "axios";
import Categories from "./categories";
import { FaHeart } from "react-icons/fa";
import './Home.css' ;

function Home() {
    const navigate = useNavigate();
    const [products, setproducts] = useState([]);
    const [cproducts, setcproducts] = useState([]);
    const [search, setsearch] = useState('');
    
   



    useEffect(() => {
        const url = 'http://localhost:4000/get-products';
        axios.get(url)
            .then((res) => {
                
                if (res.data.products) {
                    console.log(res.data.products) ;
                    setproducts(res.data.products);
                }
            })
            .catch((err) => {
                console.log(err);
                alert('server error');
            })
    }, []) ;



    const handlesearch = (value) => {

        setsearch(value);
    }

    const handlesearchbtn = () => {
        //   console.log("products" ,products) ;
        const url = "https://localhost:4000/\searchProducts?query="+search ;
        // let searchProducts = products.filter((item) => {
         
        //     if (item.pname.toLowerCase().includes(search.toLowerCase()) ||
        //         item.pdesc.toLowerCase().includes(search.toLowerCase()) ||
        //         item.ptype.toLowerCase().includes(search.toLowerCase())) {
        //         return item;
        //     }
        // })

        // setcproducts(searchProducts);

        axios.get(url)
        .then((res) => {
            console.log(res) ;
            setcproducts(res.data.products);
        })
        .catch((err) => {
            
            alert('server error');
        })

    }


    const handleCategory = (value) => {
        
        let searchProducts = products.filter((item) => {
            
            if (item.ptype.toLowerCase() == (value.toLowerCase())) {
                return item;
            }
        })

        setcproducts(searchProducts);
    }

   
    const handleLike = (value) => {

      
        const userId  = localStorage.getItem('userId');
        const productId = value ;
        
        const data = { userId, productId };
        const url = "http://localhost:4000/like-products";
        axios.post(url, data)
            .then((res) => {
                 console.log(res) ;
                 
            })
            .catch((err) => {
               alert("An error occurred. Please try again later.");
    });
        

        
             
    }


    const handleproductDetail = (value)=>{
         navigate('/ProductDetail/'+value)
    }




    return (
        <div>
            <Header search={search} handlesearch={handlesearch} handlesearchbtn={handlesearchbtn} />
            <Categories handleCategory={handleCategory} />
             
            {
                cproducts && cproducts.length > 0 ?
                <h5>SEARCH PRODUCTS:</h5> :
                <h5>NO PRODUCT FOUND</h5>

            }
            <div className="d-flex justify-content-center flex-wrap">
                {
                    products && products.length > 0 &&
                    cproducts.map((item, index) => {
                        return (
                            <div key={item._id} className="card m-3">
                                <img width="250px" height="150px" src={'http://localhost:4000/' + item.pimage}></img>
                                <h3 className="m-2 price-text">Rs. {item.pprice} /-</h3>
                                <p className="m-2">{item.pname} | {item.ptype}</p>
                                <p className="m-2 text-primary">{item.pdesc}</p>
                            </div>

                        )
                    })
                }
            </div>

            <h5>ALL PRODUCTS:</h5>
            <div className="d-flex justify-content-center flex-wrap">
                {
                    products && products.length > 0 &&
                    products.map((item, index) => {
                        return (
                            <div  onClick={()=>handleproductDetail(item._id)} key={item._id} className="card m-3">
                                <img width="250px" height="150px" src={'http://localhost:4000/' + item.pimage}></img>
                                <div className="icon-con" onClick={()=>handleLike(item._id)}>
                                    <FaHeart className="icons" />
                                </div>
                                <h3 className="m-2 price-text">Rs. {item.pprice} /-</h3>
                                <p className="m-2">{item.pname} | {item.ptype}</p>
                                <p className="m-2 text-primary">{item.pdesc}</p>
                            </div>

                        )
                    })
                }
            </div>
             


        </div>
    )
}

export default Home;
